select preco_total, data_venda, hora_venda, tipo_pagamento,nome_produto, preco_produto, quantidade, cnpj_juridica, p.nome_pessoa, cpf_fisica ,p.email_pessoa from venda v inner join pessoa_juridica pj on pj.id_juridica = v.id_restaurante inner join pessoa p on v.id_pessoa = p.id_pessoa inner join pessoa_fisica pf on p.id_pessoa = pf.id_pessoa inner join produto pr on v.id_produto = pr.id_produto